 %% Create and initialize a JScrollBar object figure(h.figure1);  tryjScrollbar = javaObjectEDT('javax.swing.JScrollBar');     jScrollbar.setOrientation(jScrollbar.HORIZONTAL);  catch     error('Cannot create Java-based scroll-bar!');  end  % Display the object onscreen  try     javacomponent(jScrollbar,'South');  catch     error('Cannot display Java-based scroll-bar!');  end   %% Create and initialize a JSlider object  tryjSlider = javaObjectEDT('javax.swing.JSlider');     jSlider.setOrientation(jSlider.VERTICAL);  catch     error('Cannot create Java-based scroll-bar!');  end  % Display the object onscreen  try     javacomponent(jSlider);  catch     error('Cannot display Java-based scroll-bar!');  end